mapper.select:
SELECT M_IDX, ID, NAME, PW, EMAIL, AUTH, USE_YN, REG_ID, REG_NAME, REG_DATE, ADDRESS, PHONE FROM TB_MEMBER WHERE USE_YN = 'Y'
AND M_IDX = #{mIdx}
AND ID = #{id}
AND NAME = #{name} AND PHONE = #{tel}
AND ID = #{findId} AND PHONE = #{tel}
limit 1

mapper.update:
UPDATE TB_MEMBER SET ADDRESS = #{address}, PHONE = #{phone}, MOD_ID = #{session.id}, MOD_NAME = #{session.name}, MOD_DATE = now(), MOD_IP = #{remoteAddr}
,PW = #{pw}
WHERE M_IDX = #{mIdx}
